import React, { useEffect, useRef, useState } from "react";

const MAX_SPIN_TIME = 5000;
const FULL_ROTATIONS = 5;

const WheelComponent = ({
  initialSegments,
  segColors,
  onFinished = () => {},
  fontFamily = "Arial",
  primaryColor = "#000",
  primaryColoraround = "#fff",
  contrastColor = "#fff",
  buttonText = "SPIN",
  isOnlyOnce = true,
}) => {
  const [segments, setSegments] = useState(initialSegments);
  const [editing, setEditing] = useState(false);
  const [inputValue, setInputValue] = useState(initialSegments.join("\n"));

  const canvasRef = useRef(null);
  const [isSpinning, setIsSpinning] = useState(false);
  const [hasFinished, setHasFinished] = useState(false);
  const [angleCurrent, setAngleCurrent] = useState(0);
  const angleRef = useRef(0);
  const animationRef = useRef(null);
  const startTimeRef = useRef(null);
  const targetAngleRef = useRef(0);

  const size = 250;
  const center = size + 10;
  const extraTop = 40;
  const canvasWidth = 3 * center;
  const canvasHeight = 2 * center + extraTop;

  const spin = () => {
    if (isSpinning || (isOnlyOnce && hasFinished)) return;
    const randomIndex = Math.floor(Math.random() * segments.length);
    const segmentAngle = (2 * Math.PI) / segments.length;
    const stopAngle =
      FULL_ROTATIONS * 2 * Math.PI -
      (randomIndex * segmentAngle + segmentAngle / 2);
    targetAngleRef.current = stopAngle;
    startTimeRef.current = performance.now();
    setIsSpinning(true);
    animate();
  };

  const animate = () => {
    const now = performance.now();
    const elapsed = now - startTimeRef.current;
    const t = Math.min(elapsed / MAX_SPIN_TIME, 1);
    const easeOut = Math.sin((t * Math.PI) / 2);
    const current =
      angleCurrent + (targetAngleRef.current - angleCurrent) * easeOut;

    angleRef.current = current;
    setAngleCurrent(current);
    drawWheel(current);

    if (t < 1) {
      animationRef.current = requestAnimationFrame(animate);
    } else {
      cancelAnimationFrame(animationRef.current);
      const result = getSegmentAtAngle(current);
      setIsSpinning(false);
      setHasFinished(true);
      onFinished(result);
    }
  };

  const getSegmentAtAngle = (angle) => {
    const segmentAngle = (2 * Math.PI) / segments.length;
    const adjusted = (2 * Math.PI - (angle % (2 * Math.PI))) % (2 * Math.PI);
    return segments[Math.floor(adjusted / segmentAngle)];
  };

  const drawWheel = (angle) => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const offsetY = extraTop / 2;
    const segmentAngle = (2 * Math.PI) / segments.length;

    for (let i = 0; i < segments.length; i++) {
      const start = angle + i * segmentAngle;
      const end = start + segmentAngle;

      ctx.beginPath();
      ctx.moveTo(center, center + offsetY);
      ctx.arc(center, center + offsetY, size, start, end);
      ctx.closePath();
      ctx.fillStyle = segColors[i % segColors.length] || "#ccc";
      ctx.fill();
      ctx.lineWidth = 0.7;
      ctx.strokeStyle = primaryColor;
      ctx.stroke();

      ctx.save();
      ctx.translate(center, center + offsetY);
      ctx.rotate(start + segmentAngle / 2);
      ctx.textAlign = "right";
      ctx.fillStyle = contrastColor;
      ctx.font = `bold 14px ${fontFamily}`;
      ctx.fillText(segments[i], size - 40, 0);
      ctx.restore();
    }

    // Cerchio centrale
    ctx.beginPath();
    ctx.arc(center, center + offsetY, 35, 0, 2 * Math.PI);
    ctx.fillStyle = primaryColor;
    ctx.fill();
    ctx.strokeStyle = contrastColor;
    ctx.lineWidth = 3;
    ctx.stroke();

    ctx.fillStyle = contrastColor;
    ctx.font = `bold 16px ${fontFamily}`;
    ctx.textAlign = "center";
    ctx.fillText(buttonText, center, center + offsetY + 5);

    // Cerchio esterno
    ctx.beginPath();
    ctx.arc(center, center + offsetY, size, 0, 2 * Math.PI);
    ctx.strokeStyle = primaryColoraround;
    ctx.lineWidth = 20;
    ctx.stroke();

    // Freccia in alto
    ctx.beginPath();
    ctx.moveTo(center - 10, center + offsetY - size - 10);
    ctx.lineTo(center + 10, center + offsetY - size - 10);
    ctx.lineTo(center, center + offsetY - size - 35);
    ctx.closePath();
    ctx.fillStyle = contrastColor;
    ctx.fill();
  };

  useEffect(() => {
    drawWheel(angleCurrent);
  }, [segments]);

  const handleEditToggle = () => setEditing(!editing);

  const handleSave = () => {
    const names = inputValue
      .split("\n")
      .map((n) => n.trim())
      .filter((n) => n !== "");
    if (names.length >= 2) {
      setSegments(names);
      setEditing(false);
      setHasFinished(false);
      setAngleCurrent(0);
    }
  };

  return (
    <div className="flex justify-center items-center flex-col w-full">
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        style={{
          width: "100%",
          height: "auto",
          cursor: isSpinning || (isOnlyOnce && hasFinished) ? "default" : "pointer",
        }}
        onClick={spin}
      />
      <div className="text-center mt-4">
        <button
          onClick={handleEditToggle}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          {editing ? "Annulla" : "Modifica nomi"}
        </button>
      </div>
      {editing && (
        <div className="w-full mt-4 px-4 max-w-md">
          <textarea
            rows={segments.length + 2}
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="w-full border border-gray-300 rounded-md p-2 font-mono resize-none"
          />
          <button
            onClick={handleSave}
            className="mt-3 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
          >
            Salva
          </button>
        </div>
      )}
    </div>
  );
};

export default WheelComponent;
